  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 100%;

  --input: 0 0% 89%;
  --ring: 211 100% 50%;
  --chart-1: 211 100% 45%;
  --chart-2: 142 71% 45%;
  --chart-3: 262 83% 58%;
  --chart-4: 36 100% 50%;
